sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var gr = new GlideAjax('Questionexample');
    gr.addParam('sysparm_name', 'getPatientDetails');
    gr.addParam('sysparm_patient_id',newValue);

    gr.getXMLAnswer(function(response) {
		var result = JSON.parse(response);
        var name = result.name;
		var age = result.age;
        g_form.setValue('u_patient_details',name);
		g_form.setValue('u_age',age);
		// g_form.showFieldMsg('u_patient_id','The name is '+ name,'error');
    });

    //Type appropriate comment here, and begin script below

}
