sys_script_include.script:
var FetchBillingDetails = Class.create();
FetchBillingDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getBillingInfo: function() {
        var billingId = this.getParameter('sysparm_billing_id');

		var billGr = new GlideRecord('u_billing');
		if(!billGr.get(billingId))
		{
			return new global.JSON().encode({
				success:false,
				error:'No Billing Record Found'
			});
		}
		return new global.JSON().encode({
			success:true,
			amount_due: billGr.getValue('u_total_amount'),
			appointment_id: billGr.getDisplayValue('u_appointment_id'),
			patient_id:billGr.getDisplayValue('u_patient_id'),
			status: billGr.getDisplayValue('u_status')
		});

    },
    type: 'FetchBillingDetails'
});
