sys_script_include.script:
var PatientDetailUtil = Class.create();
PatientDetailUtil.prototype = {

  initialize: function(patientid) {
    // store the sys_id passed in
    this.patientid = patientid;
  },

  /**
   * Returns an object with patient details.
   * Throws an exception if record not found.
   */
  getPatientDetail: function() {
    var gr = new GlideRecord('u_patient_record');
    if (!gr.get(this.patientid)) {
      throw 'Patient record not found for sys_id: ' + this.patientid;
    }

    // build the result
    return {
      name:   gr.getValue('u_first_name') + ' ' + gr.getValue('u_last_name'),
      age:    parseInt(gr.getValue('u_age'), 10),
      gender: gr.getDisplayValue('u_gender'),
      vip:    gr.getValue('u_vip') === 'true'
    };
  },

  type: 'PatientDetailUtil'
};
